// src/components/BlogList.js

import React from 'react';
import { Link } from 'react-router-dom';

const posts = [
  {
    id: 'post1',
    title: 'Breathing is bi-directional',
    summary: 'Summary of ',
    imageUrl: '/posts-images/breathing-is-bidirectional.jpg',
  },
  {
    id: 'post2',
    title: 'Second Blog Post',
    summary: 'Delving deeper into the second topic.',
    imageUrl: '/posts-images/breathing-is-bidirectional.jpg',
  },
];

function BlogList() {
  return (
    <main>
      {posts.map((post) => (
        <section key={post.id} className="postList-section">
          <div className="postList-image">
            <Link to={`/post/${post.id}`}>
              <img src={post.imageUrl} alt={post.title} className="postList-image" />
            </Link>
          </div>
          <div className="postList-text">
            <Link to={`/post/${post.id}`}>
              <h3>{post.title}</h3>
            </Link>
            <p>{post.summary}</p>
          </div>
      </section>
      ))}
    </main>
  );
}

export default BlogList;
